			e.preventDefault();
			if(!$(this).hasClass("open")){
				$("#gb-search-bottom").slideDown(function(){
					$("#gb-search-button").removeClass("closed").addClass("open");
					$(this).removeClass("closed").addClass("open");
				});
			} else {
				$("#gb-search-bottom").slideUp(function(){
					$("#gb-search-button").removeClass("open").addClass("closed");
					$(this).removeClass("open").addClass("closed");
