	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	//REMOVE LOGO ERROR IF NONE SELECTED
	function removeLogo(){
		$("#gb-logo").remove();
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var infoCheck = 'true';
		var gradesCheck = 'true';
		var facebookCheck = 'true';
		var twitterCheck = 'true';
		var googlePlayCheck = 'false';
		var instagramCheck = 'true';

		// SHOW THE ICONS THAT ARE NEEDED

		if(infoCheck == "true"){
		        var infoTarget = 'Same Window';
		        if(infoTarget == "New Window"){
		        	infoTarget = "_blank";
		        } else {
		        infoTarget = "_self";
		        }
		        $(".icn.info").removeClass("hidden").attr("target", infoTarget);
		        hasIcons = 1;
		}
		if(gradesCheck == "true"){
		        var gradesTarget = 'New Window';
		        if(gradesTarget == "New Window"){
		        	gradesTarget = "_blank";
		        } else {
		        	gradesTarget = "_self";
		        }
		        $(".icn.grades").removeClass("hidden").attr("target", gradesTarget);
		        hasIcons = 1;
		}
		if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
	    	if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}
		if(googlePlayCheck == "true"){
		        var googlePlayTarget = 'Same Window';
		        if(googlePlayTarget == "New Window"){
		        	googlePlayTarget = "_blank";
		        } else {
		        	googlePlayTarget = "_self";
		        }
		        $(".icn.play").removeClass("hidden").attr("target", playTarget);
		        hasIcons = 1;
		}
		if(instagramCheck == "true"){
		        var instagramTarget = 'New Window';
		        if(instagramTarget == "New Window"){
		        	instagramTarget = "_blank";
		        } else {
		        	instagramTarget = "_self";
		        }
		        $(".icn.instagram").removeClass("hidden").attr("target", instagramTarget);
		        hasIcons = 1;
		}
	}

	function spImage(){
		var channelName = jQuery.trim("").toLowerCase();
		if(channelName == ""){
			channelName = "misc";
		}
		channelName = channelName.replace(/\s+/g, "-");

		// GET RANDOM IMAGE FROM template_assets/subpage-images/ FOLDER FOR SUBPAGE
		if($(".sp").length){
			$.ajax({
				type: "GET",
				url: "/site/handlers/getimagesxml.ashx",
				data: "SiteID=1&base_folder=subpage-images&folderName=" + channelName,
				dataType: "xml",
				cache: false,
				async: false,
				success: function(data){
					var imgNum = $(data).find("img").length;
					if(imgNum > 1){
						var randomImg = Math.floor(Math.random() * imgNum);
						var imgSrc = "/cms/lib/NJ01000221/Centricity/Domain/1/subpage-images/" + channelName + "/" + $(data).find("img").eq(randomImg).attr("src");
						$("body").css({
							"background-image": "url(" + imgSrc + ")",
							"background-repeat": "no-repeat",
							"background-position": "center top",
							"-webkit-background-size": "cover",
							"-moz-background-size": "cover",
							"-o-background-size": "cover",
							"background-size": "cover"	
						});
					} else if(imgNum == 1){
						var imgSrc = "/cms/lib/NJ01000221/Centricity/Domain/1/subpage-images/" + channelName + "/" + $(data).find("img").eq(0).attr("src");
						$("body").css({
							"background-image": "url(" + imgSrc + ")",
							"background-repeat": "no-repeat",
							"background-position": "center top",
							"-webkit-background-size": "cover",
							"-moz-background-size": "cover",
							"-o-background-size": "cover",
							"background-size": "cover"	
						});
					}
				}
			});
		}
	}

