                                            $('#sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-startstopNav').append('Pause');
                                            $('#sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-slideShowControlButton').off('click');
                                            $('#sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-slideShowControlButton').on('click', function (event) {
                                                event.preventDefault();
                                                if ($(this).hasClass('pause')) {
                                                    $(this).removeClass('pause');
                                                    $(this).addClass('play');
                                                    $(this).find('em').text('Play');
                                                    slider.data('nivoslider').stop();
                                                }
                                                else {
                                                    $(this).removeClass('play');
                                                    $(this).addClass('pause');
                                                    $(this).find('em').text('Pause');
                                                    slider.data('nivoslider').start();
                                                }
                                            });

                                            $('#sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-prevNav, #sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-nextNav, #sliderctl00_ContentPlaceHolder1_ctl53_divView .nivo-control').attr('href', '#');  
