;$(function() { $('nav#mobile-nav').mmenu({"slidingSubmenus":false,"position":"left","zposition":"back"}, {"pageNodetype":"form"});});function isChildOf(parentId,element) {
    while(element) {
       if(element.id&&element.id.indexOf(parentId)> -1) {
          return true; 

       } 
       element=element.parentNode; 
   } 

   return false;
}
function onRowDroppingctl00_ContentPlaceHolder1_ctl56_grdPendingImageOrders(sender,args) {
   if(sender.get_id()== 'ctl00_ContentPlaceHolder1_ctl56_grdPendingImageOrders') {
      var node=args.get_destinationHtmlElement();
      if(!isChildOf('ctl00_ContentPlaceHolder1_ctl56_grdPendingImageOrders',node)) {
         args.set_cancel(true);
      }
   }
} 

