                            if(v.text.trim() != '') {
                            	html += "
" + v.text + "";
                            }

                            html += "
"+
                            "
" +
                            "

" + v.header + "" +
                            "";


                        //--> Check if there links are filled out.
                        if(v.sublink1.text.trim() != '') {

                            html += "
" + v.sublink1.text + "";

                        }
                        if(v.sublink2.text.trim() != '') {

                            html += "
" + v.sublink2.text + "";

                        }
                        if(v.sublink3.text.trim() != '') {

                            html += "
" + v.sublink3.text + "";

                        }
                        if(v.sublink4.text.trim() != '') {

                            html += "
