function resizeSlider_ir2788() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("960");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir2788-ir2788-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir2788-ir2788-slider").style.height = Math.max((parseFloat("400")/parseFloat("959") * containerWidth), parseFloat("400")) + "px";
	document.getElementById("ir2788-ir2788-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

