            });*/

            //$(".sw-channel-item").unbind('click');
            $(".sw-channel-item > a").not("#navc-HP.sw-channel-item > a").click(function(e){
            	var channelText = $("> span",this).text();
            	if(channelText != "Faculty"){
                    e.preventDefault();
                    $(".sw-channel-item .sw-channel-dropdown-outer").stop(true, true);
                    if(!$(this).hasClass("open")) {
                    		$(".sw-channel-item > a.open").removeClass("open").parent().removeClass("open").find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                            $(this).parent().find(".sw-channel-dropdown-outer").slideDown(300, "swing");
                            $(this).addClass("open").parent().addClass("open");
                        } else {
                            $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                            $(this).removeClass("open").parent().removeClass("open");
                        }    
                }    

            });
            /* end additions for case#3963221 */

            /*$(".sw-channel-item > a").not("#navc-HP.sw-channel-item > a").focusout(function(e){
            	$(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                $(this).removeClass("open").parent().removeClass("open");
            });*/

            var template = this;

            $(".sw-channel-item > a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        if(e.shiftKey) {

                            if($(this).parent().hasClass("first")) {
                                // FOCUS FIRST ITEM
                                //$(this).parent().parent().find("li.last > a").focus();
                                $("#gb-logo a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");

                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().prev("li").find("> a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");
                            }

                        } else {

                            if($(this).parent().hasClass("last")) {
                                // FOCUS FIRST ITEM
                                //$(this).parent().parent().find("li.first > a").focus();
                                $("#gb-schoollist .sw-mystart-dropdown.schoollist").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");

                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().next("li").find("> a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");
                            }

                        }    

                    break;

                    case template.KeyCodes.right:
                        e.preventDefault();

                            if($(this).parent().hasClass("last")) {
                                // FOCUS FIRST ITEM
                                $(this).parent().parent().find("li.first > a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");

                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().next("li").find("> a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");
                            }
                    break;

                    case template.KeyCodes.left:
                        e.preventDefault();

                            if($(this).parent().hasClass("first")) {
                                // FOCUS FIRST ITEM
                                $(this).parent().parent().find("li.last > a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");

                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().prev("li").find("> a").focus();
                                $(this).parent().find(".sw-channel-dropdown-outer").slideUp(300, "swing");
                                $(this).removeClass("open").parent().removeClass("open");
                            }
                    break;

                }
            });  

