//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    var headerHeight = $("#gb-header-outer").height();
                    //$(".hp main").css("margin-top",headerHeight);
                    $(".hp #hp-slideshow-outer").css("margin-top",headerHeight); /* added by Matt - case#3990173 - 11.26.18 */
                    $(".sp #gb-breadcrumbs-outer").css("margin-top",headerHeight);
                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "AllyClickForButtons": function(event) {
            if(event.type === 'click'){
                return true;
            }
            else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if(code === 13){
                    return true;
                }
            }
            else{
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },


