        "MyStart": function() {

        	var template = this;

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search-container");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

            //REPLACE MYSTART DROPDOWN ARROWS
            $(".sw-myaccount-chevron").text("▼");

            //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
            $(".sw-mystart-dropdown").addClass("transition");

            //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
            $(".sw-mystart-button").addClass("transition");

            //APPEND MYSTART ITEMS TO APPROPRIATE PLACES

            $(".sw-mystart-button.signin").appendTo("#gb-mystart .gb-mystart.right");
            $(".sw-mystart-button.register").appendTo("#gb-mystart .gb-mystart.right");


            $('#sw-mystart-left .sw-mystart-button.home a span').text("WHRSD Home");
            $('#sw-mystart-left .sw-mystart-button.home').prependTo("#gb-mystart .gb-mystart.right");


            $("#sw-myaccount").removeAttr("tabindex");
            $("#sw-mystart-account").attr("tabindex","0");

            //DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
            $("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
            $("#sw-mystart-account").off();
            $("#sw-mystart-account").click(function(){
                $(".icon-font-arrow",this).toggleClass("open");
                $("#cs-sw-myaccount-list", this).slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
                $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
            });

            $("#gb-mystart #sw-mystart-account").prepend('').attr({
            						"role": "button",
                                    "aria-expanded": "false",
                                    "aria-haspopup": "true"
            });

            $("#cs-sw-myaccount-list li").each(function(){
                $("> a", this).attr("tabindex", "0"); 
            }); 

            $("#sw-mystart-account").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

						$(".icon-font-arrow",this).toggleClass("open");
                        $("#cs-sw-myaccount-list",this).slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                        if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                            $(this).addClass("open").attr("aria-expanded","true");
                            $("#cs-sw-myaccount-list", this).find("li:first-child a").focus();
                        } else {
                            $(this).removeClass("open").attr("aria-expanded","false");
                        }
                        // FOCUS FIRST ITEM

                    break;
                }
            });    

            $("#cs-sw-myaccount-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    break;
                }
            });

            //REMOVE DUPLICATE IDs
            $("#sw-mystart-left #ui-btn-myview").remove();
            $("#sw-mystart-right #ui-btn-sitemanager").remove();

            //$('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

            //CHANGE THE SCHOOLLIST TEXT
            $(".sw-mystart-dropdown.schoollist .selector").text("Select A School");

            //ADD THE SCHOOLLIST INTO GB-MYSTART
            $(".sw-mystart-dropdown.schoollist").prependTo("#gb-schoollist");

            this.Translator();

            $(".sw-mystart-dropdown").each(function(i) {
                // SET ATTRIBUTES
                $(this).attr({
                		"role": "button",
                        "aria-expanded": "false",
                        "aria-haspopup": "true"
                        });
                $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
                $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id")).attr("aria-hidden","true");
                $(".sw-dropdown", this).attr("aria-hidden", "true");
                $(".sw-dropdown-list li", this).attr("role", "menuitem");
            });

            //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
            $(".sw-mystart-dropdown").off();
            $(".sw-mystart-dropdown").click(function() {
                  $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
                  $(".sw-dropdown-arrow",this).toggleClass("open");
                  $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");  
                  $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 
            });

            $(".sw-mystart-dropdown").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 

                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                          if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                              $(this).addClass("open").attr("aria-expanded","true");
                              $(".sw-dropdown-list li:first-child a", this).focus();

                          } else {
                                $(this).removeClass("open").attr("aria-expanded","false");
                          }
                          $(".sw-dropdown-list li a", this).attr("tabindex","0");
                        // FOCUS FIRST ITEM

                    break;
                }
            });

            $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;
                }
            });
        },
        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/NJ01001092/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('');
            var siteNameTwo = jQuery.trim('');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Warren Hills Regional School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }
        },
