        "SLM":  function() {
            $.schoolListMinibase({
                'miniBaseDomainID': 1,
                'miniBaseModuleInstanceID': 9075,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    //$('.cs-mystart-dropdown.schoollist .cs-dropdown-list').html(schools);
                    $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                    window.csTemplate.RsMenu(schoolList);
                }
            });




        },

        "SLMAccessibility": function(){

        	var template = this;

        	$(".cs-mystart-dropdown").each(function(i) {
                // SET ATTRIBUTES
                $(this).attr({
                		"role": "button",
                        "aria-expanded": "false",
                        "aria-haspopup": "true"
                        });
                $(".cs-selector", this).attr("id", "cs-mystart-dropdown-" + (i + 1));
                $(".cs-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".cs-selector", this).attr("id")).attr("aria-hidden","true");
                $(".cs-dropdown", this).attr("aria-hidden", "true");
                $(".cs-dropdown-list li", this).attr("role", "menuitem");
            });

            //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
            $(".cs-mystart-dropdown").off();
            $(".cs-mystart-dropdown").click(function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();
                  $(".cs-dropdown-arrow",this).toggleClass("open");
                  $(".cs-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".cs-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");  
                  $(".cs-dropdown-list", this).attr("aria-hidden", ($(".cs-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 
                  // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                  if(!$(this).hasClass("open")) {
                      // TOGGLE THE DROPDOWN
                      $(this).addClass("open").attr("aria-expanded","true");
                      $(".cs-dropdown-list li:first-child a", this).focus();

                  } else {
                        $(this).removeClass("open").attr("aria-expanded","false");
                  }
                  $(".cs-dropdown-list li a", this).attr("tabindex","0");
                }  
            });

            $(".cs-mystart-dropdown").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    //case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        $(".cs-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".cs-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".cs-dropdown-list", this).attr("aria-hidden", ($(".cs-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 

                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                          if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                              $(this).addClass("open").attr("aria-expanded","true");
                              $(".cs-dropdown-list li:first-child a", this).focus();

                          } else {
                                $(this).removeClass("open").attr("aria-expanded","false");
                          }
                          $(".cs-dropdown-list li a", this).attr("tabindex","0");
                        // FOCUS FIRST ITEM

                    break;
                }
            });

            $(".cs-dropdown .cs-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {

                            // FOCUS LAST ITEM
                            //$(this).closest(".cs-dropdown-list").find("li:last-child > a").focus();
                            $(this).parent().parent().find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            //$(this).closest(".cs-dropdown-list").find("li:first-child > a").focus();
                            $(this).parent().parent().find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".cs-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".cs-dropdown-list").find("li:last-child > a").focus();
                    break;
                }
            });
        },

