                    break;
                }
            });
        },

        "HomepageEvents": function(){

            // FOR SCOPE
            var template = this;

            var totalEventDays = $(".hp-content-row.one .upcomingevents li").length;
            var viewCalendarURL = $(".hp-content-row.one .upcomingevents .view-calendar-link").attr("href");

            if(totalEventDays > 5 && $(".hp-content-row.one .upcomingevents").length){
                //ADD VIEW CALENDAR LINK AND EXPAND EVENTS BUTTON
                $(".hp-content-row.one").append("

View MoreView Full Calendar");

                eventA11y();

                $("#view-btn").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                        //DONT LET THE PAGE JUMP ON KEYPRESS
                        event.preventDefault();

                        var events = $(".hp-content-row.one");
                        var clickedButton = $(".btn-text",this);
                        var clickedBtnArrow = $(".icon-font-arrow2",this);
                        var initialHeight = 130;

                        if(events.hasClass("open")){
                        	clickedButton.removeClass("open").text("View More");
                                clickedBtnArrow.removeClass("open");
                            events.animate({
                                height: initialHeight

                            }, 500, function() {
                                eventA11y();
                                events.removeClass("open");
                            });
                        } else {
                            var totalHeight = events.get(0).scrollHeight - 70;
							clickedButton.addClass("open").text("View Less");
                            clickedBtnArrow.addClass("open");
                            events.animate({
                                height: totalHeight
                            }, 500, function() {
                                events.find("a").attr("tabindex","0");
                                events.addClass("open");
                            });
                        } 
                    }
                }) 

            } else {
                //ADD VIEW CALENDAR LINK
                if($(".hp-content-row.one .upcomingevents").length){
                	$(".hp-content-row.one").append("

View Full Calendar");
                } else {
                	$(".hp-content-row.one").addClass("no-events");
                }
            }

            function eventA11y(){
                $(".hp-content-row.one .upcomingevents li p").each(function(){
                    var elementPosition = $(this).position().top;
                    if(elementPosition > 30){

                        $(this).find("a").attr("tabindex","-1");
                    }

                });
            }
