main.script:
$(document).ready(function(){	
	var autoplayspeedis = parseInt('60')*1000;
	$('#event-slider10').slick({
  		infinite: true,
		dots: true,
		arrows: true,
		autoplay: true,
		slidesToShow: '4',
		slidesToScroll: 1,
		autoplaySpeed: autoplayspeedis,
		responsive: [
		    {
		      breakpoint: 1024,
		      settings: {
		        slidesToShow: 3,
		        slidesToScroll: 3,
		        infinite: true,
		        dots: true
		      }
		    },
		    {
		      breakpoint: 600,
		      settings: {
		        slidesToShow: 2,
		        slidesToScroll: 2
		      }
		    },
		    {
		      breakpoint: 480,
		      settings: {
		        slidesToShow: 1,
		        slidesToScroll: 1
		      }
		    }
		    // You can unslick at a given breakpoint now by adding:
		    // settings: "unslick"
		    // instead of a settings object
		  ]
	});
});

