body.script:
(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.9&appId=517268555036631";
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

$(document).ready(function(){
	var ww = $(window).width();
	if(ww < 992){
		$('.submenu').click(function(){		
			$(this).parent('li').toggleClass('open');		
			$(this).children('i').toggleClass('fa-minus');
			$(this).parent('li').siblings('li').removeClass('open').children('.submenu').children('i').removeClass('fa-minus');
		});
	}	
})
$(window).on("resize",function(){
	var ww = $(window).width();	
	if(ww > 991){
		$('li').removeClass('open');
		$('.submenu').children('i').removeClass('fa-minus');		
	}
})
