function resizeSlider_ir3834() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3834-ir3834-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3834-ir3834-slider").style.height = Math.max((parseFloat("414")/parseFloat("1200") * containerWidth), parseFloat("414")) + "px";
	document.getElementById("ir3834-ir3834-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir3834(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir3834(); });
}
resizeSlider_ir3834();

