$(document).ready(function () {
    $(".LiquidButton").click(function () {
        if ($(".LiquidButton").attr("class").includes("LiquidDown")) {
            $(".LiquidHeader").addClass("Open");
            $(".LiquidHeader").addClass("navbar-fixed-top");
            var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 51;
            $('#TopUCs').css('margin-top', mt + 'px')
            $(".LiquidButton").addClass("LiquidUp");
            $(".LiquidButton").removeClass("LiquidDown");
        } else {
            $(".LiquidHeader").removeClass("Open");
            $(".LiquidHeader").removeClass("navbar-fixed-top");
            
            $(".LiquidButton").removeClass("LiquidUp");
            $(".LiquidButton").addClass("LiquidDown");
            var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) - 51;
            $('#TopUCs').css('margin-top', mt + 'px')
        }
    });
    if ($(".LiquidHeader").hasClass("Open")) {
        $(".LiquidHeader").addClass("navbar-fixed-top");
        var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 51;
        $('#TopUCs').css('margin-top', mt + 'px')
        $(".LiquidButton").removeClass("LiquidDown");
        $(".LiquidButton").addClass("LiquidUp");
    }
});
$(document).konami({
    result: function () {
        var c = document.createElement("canvas");
        var ctx = c.getContext("2d");
        c.width = 500;
        c.height = 500;
        var colors = ["#FF2211", "#00BB00", "#2233FF", "#F0E800", "#F07500", "#FF33FF", "#000000"];
        function KCluster(k) {
            var _k = k;
            var centeroidList = [];
            var cluster = this;
            var pointList = [];
            var minNum = undefined;
            var maxNum = undefined;
            this.addPoint = function (point) {
                var data = { "point": point, "cluster": 0 };
                if (minNum == undefined) {
                    var a = point.getArgs();
                    minNum = [];
                    maxNum = [];
                    for (var i = 0; i < a.length; i++) {
                        minNum.push(a[i]);
                        maxNum.push(a[i]);
                    }
                } else {
                    var a = point.getArgs();
                    for (var i = 0; i < a.length; i++) {
                        if (minNum[i] > a[i]) minNum[i] = a[i];
                        if (maxNum[i] < a[i]) maxNum[i] = a[i];
                    }
                }
                pointList.push(data);
            }
            this.getPointList = function () {
                return pointList;
            }
            this.getCenteroids = function () {
                return centeroidList;
            }
            this.initializeCenteroids = function () {
                var _n = pointList[0].point.getArgs();
                centeroidList = [];
                for (var i = 0; i < _k; i++) {
                    var r = [];
                    for (var j = 0; j < _n.length; j++) {
                        var diff = maxNum[j] - minNum[j];
                        r.push(Math.random() * diff + minNum[j]);
                    }
                    centeroidList.push(r);
                }
            }
            this.updateCenteroids = function () {
                var cont = true;
                var prevCent = [];
                for (var i = 0; i < _k; i++) {
                    prevCent.push(centeroidList[i]);
                }
                while (cont) {
                    for (var i = 0; i < pointList.length; i++) {
                        var smallest = window.Infinity;
                        for (var j = 0; j < centeroidList.length; j++) {
                            var p = pointList[i].point;
                            var c = centeroidList[j];
                            var pArgs = p.getArgs();
                            var d = Math.distance(pArgs, c);
                            if (d < smallest) {
                                smallest = d;
                                pointList[i].cluster = j;
                            }
                        }
                    }
                    for (var i = 0; i < centeroidList.length; i++) {
                        var filtered = pointList.filter(function (point) {
                            return point.cluster == i;
                        });
                        for (var j = 0; j < centeroidList[i].length; j++) {
                            prevCent[i][j] = centeroidList[i][j];
                        }
                        if (filtered.length > 0) {
                            centeroidList[i] = Math.center(filtered);
                        } else {
                            for (var k = 0; k < centeroidList[i].length; k++) {
                                var diff = maxNum[k] - minNum[k];
                                centeroidList[i][k] = Math.random() * diff + minNum[k]
                            }
                        }
                    }
                    cont = false;
                    for (var i = 0; i < centeroidList.length; i++) {
                        for (var j = 0; j < centeroidList[i].length; j++) {
                            if (prevCent[i][j] != centeroidList[i][j] && !cont) {
                                cont = true;
                                break;
                            }
                        }
                    }
                };
            }
        }
        function ClusterPoint(args) {
            var _args = args;
            var point = this;
            this.getArgs = function () {
                return args;
            }
        }
        Math.distance = function (args1, args2) {
            var result = 0;
            for (var i = 0; i < args1.length; i++) {
                result += Math.pow((args1[i] - args2[i]), 2);
            }
            return Math.sqrt(result);
        }
        Math.center = function (args) {
            var result = [];
            var n = args[0].point.getArgs().length;
            for (var i = 0; i < n; i++) {
                result.push(0);
            }
            for (var i = 0; i < args.length; i++) {
                var a = args[i].point.getArgs();
                for (var j = 0; j < n; j++) {
                    result[j] += a[j] / args.length;
                }
            }
            return result;
        }
        var input = document.createElement("input");
        input.type = "number";
        input.min = 1;
        input.max = 1;
        input.value = 1;
        function Draw(){
            ctx.clearRect(0, 0, c.width, c.height);
            var kclust = new KCluster(input.value);
            for (var i = 0; i < dataList.length; i++) {
                kclust.addPoint(new ClusterPoint(dataList[i]));
            }
            kclust.initializeCenteroids();
            kclust.updateCenteroids();
            var list = kclust.getPointList();
            for (p of list){
                ctx.fillStyle = colors[p.cluster];
                var a = p.point.getArgs();
                ctx.beginPath();
                ctx.arc(a[0], a[1], 5, 0, 2 * Math.PI);
                ctx.fill();
            }
        }
        var outerContainer = document.createElement("div");
        var innerContainer = document.createElement("div");
        var dataList = [];
        c.addEventListener("click", function(e){
            var nPoint = [e.offsetX, e.offsetY];
            dataList.push(nPoint);
            input.max = Math.min(colors.length, dataList.length);
            Draw();
        });
        input.addEventListener("change", function(e){
            Draw();
        });
        var span = document.createElement("span");
        var clear = document.createElement("a");
        var close = document.createElement("a");
        clear.addEventListener("click", function(e){
            ctx.clearRect(0, 0, c.width, c.height);
            dataList = [];
            input.value = 1;
            input.max = 1;
        });
        close.addEventListener("click", function(e){
            $(outerContainer).remove();
        });
        close.innerHTML = "×";
        clear.innerHTML = "Clear";
        close.setAttribute("style", "cursor: pointer; text-decoration: none; font-size: 42px; font-weight: 400; position: absolute; top: 15px; right: 15px; line-height: 1; color: white;");
        clear.setAttribute("style", "cursor: pointer; text-decoration: none; font-size: 14px; position: relative; top: -2px; font-weight: 400; line-height: 24px; border: 1px solid #999; background: #CCC; color: black; display: inline-block; padding: 0px 6px; margin-left: 6px;");
        span.innerHTML = "# of Groups: ";
        span.style.display = "inline-block";
        span.style.marginLeft = "10px";
        span.style.marginRight = "5px";
        $(outerContainer).attr("style", "width: 100%; height: 100%; position: fixed; background: rgba(0, 0, 0, 0.4); left: 0; top: 0; z-index: 9999;");
        $(innerContainer).attr("style", "width: 520px; height: 565px; position: absolute; background: white; left: 50%; top: 50%; transform: translate(-50%, -50%);");
        $(c).attr("style", "border: 1px solid black; margin-left: 10px; margin-top: 10px; margin-bottom: 10px;");
        $(c).appendTo(innerContainer);
        $(span).appendTo(innerContainer);
        $(input).appendTo(innerContainer);
        $(clear).appendTo(innerContainer);
        $(innerContainer).appendTo(outerContainer);
        $(close).appendTo(outerContainer);
        $(outerContainer).appendTo("body");
    }
});

