				return [false,""];
			  }
			}
			$('#datepicker').datepicker({ 
				beforeShowDay: available,
				onSelect: function(date){
					var calDate = date;
					$('#load-cal').load('load-calendar.cfm?cd='+calDate);
					$(".custom-menu").hide(100);
					$(".custom-menu").finish().show(100).
					css({
						top: event.pageY + "px",
						left: event.pageX + "px"
					});
					$.ajax({
						type: "POST",
						cache: false,
						url: "selectCalendar.cfm",
						data: {
							date : date,
						},
						success: function(data){
							$('.cal-result').html(data);
						}
					});
					$('.option-1').text(date);
				}
			});
