        //Initial load of page
        $(document).ready(function () { 
            sizeContent();
            swapSidebar();
        });

        //Every resize of window
        $(window).resize(function () {
            sizeContent();
            swapSidebar();
        });

        //Dynamically assign height
        function sizeContent() {
            if ($(window).width() > 991) {
                var newHeight = $(".inner-right-col").height();
                $(".inner-left-col").css("min-height", newHeight + 40);
            } else {
                 $(".inner-left-col").css("min-height", 300);
            }
        }

        function swapSidebar() {
            var $body, $mainContainer, $firstDiv;

            $body = $('body'),
            $mainContainer = $('.main-container-row'),
            $firstDiv = $('.main-container-row > div:first-child');

            if (($(window).width() < 992) && $body.hasClass('schools')) {
                if ($firstDiv.hasClass('inner-right-col')) {
                    $firstDiv.detach().appendTo($mainContainer);
                }
            } else if (($(window).width() > 991) && $body.hasClass('schools')) {
                if ($firstDiv.hasClass('inner-left-col')) {
                    $firstDiv.detach().appendTo($mainContainer);
                }
            }
        }

        $(function () {
            $('.top-btn').click(function (e) {
                e.preventDefault();
                $('html, body').animate({ scrollTop: '0px' }, 300);
            });
        });

