meta.script:
    
    $(function(){
        $('.sw-tabBar [class*="sw-tab"]').click(function(){
            switchTabs($(this));
        });
        $('.sw-tab'+'1').addClass('active');
    }); 
    
    //accepts the object clicked, and changes the current tab to be that one, if applicable
    function switchTabs(curElem){
		var tabScope = curElem.parent().parent();
		var activeElem = tabScope.find('.active');
		var curIndex = curElem.attr("rel");
		var activeIndex = activeElem.attr("rel");
		
		if(curIndex != activeIndex){
			curElem.toggleClass('active');
			activeElem.toggleClass('active');
			tabScope.children('.sw-tab'+activeIndex+'Content').slideUp( function(){ 
				tabScope.children('.sw-tab'+curIndex+'Content').slideDown( function(){
					tabScope.children('.sw-tab'+curIndex+'Content').css("minHeight", "1%"); 
				}); 
			});
		}//endif
    }

