	function modalPopup(srcUrl){
		$.modal('
',{
			onOpen: function (dialog) {dialog.overlay.fadeIn('fast', function () {dialog.data.hide();dialog.container.fadeIn('normal', function () {dialog.data.slideDown('fast');});});},
			onClose: function (dialog) {dialog.data.slideUp('fast', function () {dialog.container.fadeOut('normal', function () {dialog.overlay.fadeOut('fast', function () {$.modal.close();});});});},
