		});//end $.modal()
	}//end modalPopup
	
	//this should only ever be called from the vodPreview file
	function modalVideo(vidToPlay){
		//log the hit using playCount - 7/18/14
		videoHit(vidToPlay);	
		var flashvars = {};
		flashvars.colorVar1 = "CD3333";
		flashvars.textVar1 = vidToPlay;
		flashvars.textVar2 = "rtmp://videos.schoolworld.com/vod/sw/";
		
						
	
		$('#sw-modalVod').modal({
			onOpen: function (dialog) {dialog.overlay.fadeIn('normal', function () {dialog.data.hide();dialog.container.fadeIn('normal', function () {
				dialog.data.show();
				
				swfobject.embedSWF("/myicons/pix/vod/videoPlayer.swf", "sw-vodPopup", "640", "360", "9", false, flashvars, params={wmode:"transparent",allowscriptaccess:"always",allowfullscreen:"true"}, attributes={});
			});});},
			onClose: function (dialog) {dialog.data.hide(); dialog.container.fadeOut('normal', function () {dialog.overlay.fadeOut('normal', function () {$.modal.close();});});},
			containerCss:{width:"640px",height:"360px"},
			opacity:"80"
		});//end $('#sw-modalVod').modal()
	}//end modalVideo
	
