function resizeSlider_ir1814() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir1814-ir1814-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir1814-ir1814-slider").style.height = Math.max((parseFloat("420")/parseFloat("960") * containerWidth), parseFloat("420")) + "px";
	document.getElementById("ir1814-ir1814-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

section.script:
function playerReady(thePlayer) {
	player = window.document[thePlayer.id];
	try{
		player.addModelListener("STATE", "window.videoInterface.onPlayerStateChange");
		player.addModelListener("ERROR", "window.videoInterface.onPlayerPlaybackError");
		player.addModelListener("TIME", "window.videoInterface.onPlayerTimeChange");
		player.addModelListener("META", "window.videoInterface.onMeta");
		player.addControllerListener("SEEK", "window.videoInterface.onPlayerSeek");
	} catch (exp) {
		if(window.console)
			console.log(exp);
	}
}

