        //Initial load of page
        $(document).ready(sizeContent);
        //Every resize of window
        $(window).resize(sizeContent);
        //Dynamically assign height
        function sizeContent() {
            var newHeight = $(".inner-right-col").height();
            $(".inner-left-col").css("min-height", newHeight + 40);
        }

        var $root = $('html, body');

        $('.linkList-Item a[href^="#"]').click(function () {
            $root.animate({
                scrollTop: $('[id="' + $.attr(this, 'href').substr(1) + '"]').offset().top - 395
            }, 1350);

            return false;
        });

        $('a[href^="#top"]').click(function () {
            $root.animate({
                scrollTop: $('[name="' + $.attr(this, 'href').substr(1) + '"]').offset().top - 500
            }, 1350);

            return false;
        });

        $(function () {
            $('.top-btn').click(function (e) {
                e.preventDefault();
                $('html, body').animate({ scrollTop: '0px' }, 300);
            });
        });

        //Remove container of calendar events
        $("#ctl00_ctl00_ContentPlaceHolderMain_ContentPlaceHolderMain_UpcomingEventsBootstrap2_pnlNormalViewEvent").removeClass("container");

        //Fade a div on scroll
        $(window).scroll(function () {

            if ($(this).scrollTop() > 0) {
                $('.fade').fadeOut();
            }
            else {
                $('.fade').fadeIn();
            }
        });


        (function () {
            'use strict';
            var he = document.getElementById('header');
            var startShrink = 50;
            window.addEventListener('scroll',
                function () {
                    if (window.pageYOffset > startShrink) {
                        he.classList.remove('grow');
                        he.classList.add('shrink');
                    }
                    else {
                        he.classList.remove('shrink');
                        he.classList.add('grow');
                    }
                }, false);
        }());

