div.script:
$(document).ready(function () {
    $("#2c934ebc-3317-4ee0-9907-732976c1063a").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#ec8f9f15-e7e4-40e5-be02-25e5af0bc982").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#ec8f9f15-e7e4-40e5-be02-25e5af0bc982').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#ec8f9f15-e7e4-40e5-be02-25e5af0bc982').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#41d177f9-4440-4e76-966f-7f118efec015').hide();
    jQuery('#41d177f9-4440-4e76-966f-7f118efec015').listSolrResults({
        currentPagePath: '/content/schools/ncva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '5',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/ncva/en/news.html',
        viewAllTitle: '',
        uuid: '41d177f9-4440-4e76-966f-7f118efec015',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

