                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "SchoolMegaMenu": function() {
        	var _this = this;

        	if($(".sw-mystart-dropdown.schoollist").length) {
                var schools = { "Elementary": [], "Middle": [], "High": [], "Special": [] };
                var schoolName = "", schoolUrl = "";
                var schoolColumns = 3;

                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list > li").each(function() {
                	schoolName = $("> a", this).text();
                    schoolUrl = $("> a", this).attr("href");

                    // ELEMENTARY
                    if(schoolName.toLowerCase().indexOf("(es)") > -1) {
                    	schools.Elementary.push({ "name": $.trim(schoolName.replace("(es)", "")), "url": schoolUrl });
                    }

                    // MIDDLE
                    else if(schoolName.toLowerCase().indexOf("(ms)") > -1) {
                    	schools.Middle.push({ "name": $.trim(schoolName.replace("(ms)", "")), "url": schoolUrl });
                    }

                    // HIGH
                    else if(schoolName.toLowerCase().indexOf("(hs)") > -1) {
                    	schools.High.push({ "name": $.trim(schoolName.replace("(hs)", "")), "url": schoolUrl });
                    }

                    // SPECIAL
                    else if(schoolName.toLowerCase().indexOf("(s)") > -1) {
                    	schools.Special.push({ "name": $.trim(schoolName.replace("(s)", "")), "url": schoolUrl });
                    }

                    // NO TAG - ADD TO SPECIAL
                    else {
                    	schools.Special.push({ "name": $.trim(schoolName), "url": schoolUrl });
                    }
                });

                $(".gb-header.right").prepend('' +
                    '
' +
                        '
Schools' +
                        '
' +
                        	'
' +
                            '
' +
                                '' +
                                    '
Elementary' +
                                    '
Middle' +
                                    '
High' +
                                    '
Special' +
                                '' +
                                '' +
                                    '
' + buildSchoolLists("Elementary") + '' +
                                    '
' + buildSchoolLists("Middle") + '' +
                                    '
' + buildSchoolLists("High") + '' +
                                    '
' + buildSchoolLists("Special") + '' +
                                '' +
                                '
' +
                                    'Close' +
                                '' +
                            '' +
                        '' +
                    '' +
                '');

                $(".cs-mystart-dropdown.schools [data-school-num='0']").remove();
                $(".cs-mystart-dropdown.schools .cs-dropdown-tab:first-child").attr("aria-selected", "true");
                $(".cs-mystart-dropdown.schools .cs-dropdown-panel:first-child").attr("aria-hidden", "false");

                // EVENTS
                $(".cs-mystart-dropdown.schools .cs-dropdown-outer-bg").click(function(e) {
                	$(".cs-mystart-dropdown.schools .cs-selector").attr("aria-expanded", "false").parent().removeClass("open");
                    $(".cs-mystart-dropdown.schools .cs-dropdown-outer").attr("aria-hidden", "true");
                    $("body").css("overflow", "");
                });

                $(".cs-mystart-dropdown.schools").on("click", ".cs-selector", function(e) {
                    e.preventDefault();

                    if($(this).parent().hasClass("open")){
                        $("+ .cs-dropdown a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                        $("body").css("overflow", "");
                    } else {
                        $(this).attr("aria-expanded", "true").parent().addClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "false");
                        $("body").css("overflow", "hidden");
                    }
                });

                $(".cs-mystart-dropdown.schools").on("keydown", ".cs-selector", function(e) {
                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case _this.KeyCodes.enter:
                        case _this.KeyCodes.space:
                            e.preventDefault();

                            // IF THE DROPDOWN IS OPEN, CLOSE IT
                            if($(".cs-mystart-dropdown.schools").hasClass("open")){
                                $("+ .cs-dropdown a", this).attr("tabindex", "-1");
                                $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                                $("body").css("overflow", "");
                            } else {
                                $(this).attr("aria-expanded", "true").parent().addClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "false");
                                $("body").css("overflow", "hidden");

                                setTimeout(function() {
                                	$(".cs-mystart-dropdown.schools .cs-dropdown-tab[aria-selected='true']").focus();
                                }, 500);
                            }
                        break;

                        // CONSUME TAB KEY
                        case _this.KeyCodes.tab:
                        	var dropdownSelector = this;

                        	setTimeout(function() {
                            	if(!$(".cs-mystart-dropdown.schools").find(":focus").length) {
                            		$("+ .cs-dropdown a", dropdownSelector).attr("tabindex", "-1");
                            		$(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                                    $("body").css("overflow", "");
                            	}
                            }, 500);
                        break;

                        // CONSUME DOWN AND RIGHT ARROWS
                        case _this.KeyCodes.down:
                        case _this.KeyCodes.right:
                            e.preventDefault();

                            $("+ .cs-dropdown a", this).attr("tabindex", "-1");
                            $("+ .cs-dropdown .cs-dropdown-tab[aria-selected='true']", this).focus();
                        break;
                    }
                });

                // TAB CLICK
                $(document).on("click", ".cs-dropdown-tab", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

						switchTabs({
                        	"selectedTab": $(this)
                        });
                    }
                });

                // TAB KEYDOWN
                $(document).on("keydown", ".cs-dropdown-tab", function(e) {
                    var lastIndex = $(".cs-dropdown-tab").length;

                    switch(e.keyCode) {
                        case _this.KeyCodes.left:
                        case _this.KeyCodes.up:
                            e.preventDefault();
                            e.stopPropagation();

                            var prevTab;
                            if (parseInt($(this).attr("data-index")) == 1) { 
                                prevTab = $(".cs-dropdown-tab[data-index='" + lastIndex + "']");
                            } else { 
                                prevTab = $(".cs-dropdown-tab[data-index='" + (parseInt($(this).attr("data-index")) - 1) + "']");
                            } 

                            switchTabs({
                                "selectedTab": prevTab
                            });
                        break;

                        case _this.KeyCodes.right:
                        case _this.KeyCodes.down:
                            e.preventDefault();
                            e.stopPropagation();

                            var nextTab;
                            if (parseInt($(this).attr("data-index")) == lastIndex) { 
                                nextTab = $(".cs-dropdown-tab[data-index='1']");
                            } else { 
                                nextTab = $(".cs-dropdown-tab[data-index='" + (parseInt($(this).attr("data-index")) + 1) + "']");
                            } 

                            switchTabs({
                                "selectedTab": nextTab
                            });
                        break;

                        case _this.KeyCodes.home:
                            e.preventDefault();
                            e.stopPropagation();

                            switchTabs({
                                "selectedTab": $(".cs-dropdown-tab:first-child")
                            });
                        break;

                        case _this.KeyCodes.end:
                            e.preventDefault();
                            e.stopPropagation();

                            switchTabs({
                                "selectedTab": $(".cs-dropdown-tab:last-child")
                            });
                        break;

                        case _this.KeyCodes.esc:
                                e.preventDefault();

                                $(".cs-dropdown-panel").attr("tabindex", "-1");
                            	$(".cs-mystart-dropdown.schools .cs-selector").attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                                $(".cs-mystart-dropdown.schools .cs-selector").focus();
                                $("body").css("overflow", "");
                            break;

                        case _this.KeyCodes.tab:
                        	if(!e.shiftKey) {
                                e.preventDefault();
                                e.stopPropagation();

                                $(".cs-dropdown-panel[aria-hidden='false'] a:first").attr("tabindex", "0").focus();
                            } else if(e.shiftKey) {
                            	e.preventDefault();
                                e.stopPropagation();

                                $("#cs-school-menu-close").focus();
                            }
                        break;
                    }
                });

                // PANEL KEYDOWN
                $(".cs-dropdown-panel").each(function() {
                	var link = $("a", this);
                    var firstLinkIndex = $(link).index($(link).first());
                    var firstLink = $(link).eq($(link).index($(link).first()));
                    var lastLinkIndex = $(link).index($(link).last());
                    var lastLink = $(link).eq($(link).index($(link).last()));

                    $(link).keydown(function(e) {
                        var increment;
                        var thisIndex = link.index(this);
                        var newIndex = -1;

                        switch(e.keyCode) {
                            case _this.KeyCodes.up:
                            case _this.KeyCodes.left:
                                e.preventDefault();

                                if($(this).is(firstLink)) {
                                    newIndex = lastLinkIndex;
                                } else {
                                    newIndex = thisIndex - 1;
                                }
                            break;

                            case _this.KeyCodes.down:
                            case _this.KeyCodes.right:
                                e.preventDefault();

                                if($(this).is(lastLink)) {
                                    newIndex = firstLinkIndex;
                                } else {
                                    newIndex = thisIndex + 1;
                                }
                            break;

                            case _this.KeyCodes.esc:
                                e.preventDefault();

                                $(link).attr("tabindex", "-1");
                            	$(".cs-mystart-dropdown.schools .cs-selector").attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                                $(".cs-mystart-dropdown.schools .cs-selector").focus();
                                $("body").css("overflow", "");
                            break;

                            case _this.KeyCodes.tab:
                                if(e.shiftKey) {
                                    e.preventDefault();
                                    e.stopPropagation();

                                    $(".cs-dropdown-tab[aria-selected='true']").focus();
                                } else {
                                	e.preventDefault();
                                    e.stopPropagation();

                                	$(link).attr("tabindex", "-1");
                            		$("#cs-school-menu-close").focus();
                                }
                            break;
                        }

                        if(newIndex != -1) {
                        	link.attr("tabindex", "-1");
                            link.eq(newIndex).attr("tabindex", "0").focus();
                        }
                    });
                });

                $(document).on("click keydown", "#cs-school-menu-close", function(e) {
                	if(_this.AllyClick(e)) {
                    	e.preventDefault();

                    	$(".cs-dropdown-panel").attr("tabindex", "-1");
                        $(".cs-mystart-dropdown.schools .cs-selector").attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown-outer").attr("aria-hidden", "true");
                        $(".cs-mystart-dropdown.schools .cs-selector").focus();
                        $("body").css("overflow", "");
                    } else {
                        switch(e.keyCode) {
                            case _this.KeyCodes.tab:
                                e.preventDefault();

                                if(e.shiftKey) {
                                    $(".cs-dropdown-panel[aria-hidden='false'] a:last").attr("tabindex", "0").focus();
                                } else {
                                    $(".cs-dropdown-tab[aria-selected='true']").focus();
                                }
                            break;
                        }
                    }
                });

                function switchTabs(props) { // props.selectedTab
                	// SWITCH TAB
                    $(".cs-dropdown-tab[aria-selected='true']").attr("aria-selected", "false").attr("tabindex", "-1");
                    $(props.selectedTab).attr("aria-selected", "true").attr("tabindex", "0").focus();

                    // SWITCH PANEL
                    var nextIndex = parseInt(props.selectedTab.attr("data-index"));
                    $(".cs-dropdown-panel").attr("aria-hidden", "true");
                    $(".cs-dropdown-panel[data-index='" + nextIndex + "']").attr("aria-hidden", "false");
                }

                function buildSchoolLists(category) {
                    var modifiedSchoolList = '';
                    var schoolLists = '';
                    var columnClasses = ["one", "two", "three"];
                    var schoolsPerColumn = Math.ceil(schools[category].length / schoolColumns);

                    for(var i = 0; i < schoolColumns; i++) {
                        modifiedSchoolList = schools[category].splice(0, schoolsPerColumn); // 13 SCHOOLS PER COLUMN

                        if(modifiedSchoolList.length) {
                        	schoolLists += '';

                            $.each(modifiedSchoolList, function(index, school) {
                                schoolLists += '
' + school.name + '';
                            });

                            schoolLists += '';
                        }
                    }

                    return schoolLists;
                }
            }
        },

