        "ModEvents": function() {
        	$(".ui-widget.app.upcomingevents").modEvents({
				columns     : "yes",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
            	if(!$(".ui-article-title.sw-calendar-block-date", this).length && $.trim($(".ui-article-description", this).text()) != "There are no upcoming events to display.") {
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".upcoming-column.right").append(moveArticle);
					$(this).parent().remove();
				} else {
                	var eventMonth = $(".joel-month", this).detach();
                    var eventDay = $(".joel-day", this).detach();
                    var eventDate = $.trim($(".ui-article-title", this).text());
                    if(eventDate.length == 1) eventDate = "0" + eventDate;

                    $(".ui-article-title", this).empty().attr("aria-label", $(eventDay).text() + ", " + $(eventMonth).text() + " " + eventDate).prepend('' + eventDate + '').append('');
                    $('' + $(eventDay).text() + '').appendTo($(".joel-month-day", this));
                    $(eventMonth).appendTo($(".joel-month-day", this));
                	$(".joel-month", this).attr("aria-hidden", "true");
                }
            });

            $(".hp .ui-widget.app.upcomingevents").each(function() {
            	var viewCalendarLink = $(".view-calendar-link", this).attr("href");

                $(".ui-articles > li", this).each(function() {
                    if($(".upcoming-column.right .ui-article-description", this).length > 2) {
                        var eventUrl = ((($(".upcoming-column.right .ui-article-description:first-child a", this).attr("href")).split("#")[1]).split("/"));
                        var calendarUrl =  viewCalendarLink + "#" + eventUrl[0] + "/" + eventUrl[1] + "/day";
                        $(".upcoming-column.right", this).append('
');
                    }
                });

                if($(this).closest(".hp-content.three").length && !$(".ui-widget-header", this).hasClass("ui-helper-hidden") && $.trim($(".ui-widget-header", this).text()) != "") {
                	$(".ui-widget-header", this).addClass("has-calendar-link").append($(".view-calendar-link", this));
                }
            });
        },

