        "CheckSlideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($(".hp").length && this.GetBreakPoint() != "desktop" && ("Multimedia Gallery" == "Multimedia Gallery;Streaming Video" || "Multimedia Gallery" == "Multimedia Gallery")) {
                if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(this.SlideshowDescFixed) {
                        $("#hp-slideshow").removeAttr("style");

                        this.SlideshowDescFixed = false;
                    }
                } else {
                    if(!this.SlideshowDescFixed) {
                        $("#hp-slideshow").css({
                            "height": $("#hp-slideshow").height(),
                            "overflow": "hidden"
                        });

                        this.SlideshowDescFixed = true;
                    }
                }
            } else if($(".hp").length && this.GetBreakPoint() == "desktop") {
            	if(this.SlideshowDescFixed) {
                    $("#hp-slideshow").removeAttr("style");

                    this.SlideshowDescFixed = false;
                }
            }
        },

