        "MmgCollage": function() {
        	var _this = this;

        	if($("#sw-content-container4.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container4.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;
                mmg.records.splice(-1, 1);

                $("#sw-content-container4.ui-hp .ui-widget.app.multimedia-gallery:first").addClass("collage");
                $("#sw-content-container4.ui-hp .ui-widget.app.multimedia-gallery:first .mmg-container").remove();

                var mmgRecords = '';
                $.each(mmg.records, function(index, record) {
                	var recordIsEmpty = (record.hideTitle == "True" && record.hideCaption == "True" && record.isLinked == "False" && record.videoIsEmbedded == "False") ? true : false;

                	mmgRecords +=	'
' +
                    					'
' +
                                        '
' +
                                        	'
' +
                                        		'
' + record.title + '' +
                                            	((record.hideCaption == "False") ? '
' + record.caption + '' : '') +
                                            	((record.isLinked == "True" || record.videoIsEmbedded == "True") ? '
' : '') +
                                            		((record.isLinked == "True") ? '' + record.linkText + '' : '') +
                                            		((record.videoIsEmbedded == "True") ? '' + record.videoLinkText + '' : '') +
                                            	((record.isLinked == "True" || record.videoIsEmbedded == "True") ? '' : '') +
                                        	'' +
                                        '' +
                                    '';

                    if(index == 5) return false;
                });

                if(mmgRecords) {
                	$("#sw-content-container4.ui-hp .ui-widget.app.multimedia-gallery:first .ui-widget-detail").prepend('' + mmgRecords + '
');
                }

                $(".ui-widget.app.multimedia-gallery.collage").on("focusin touchstart", "li", function(e) {
                	$(this).addClass("focus");
                    $(".ui-widget.app.multimedia-gallery.collage .cs-mobile-collage-description").html($(".ui-article-inner", this).html());
                }).on("focusout", "li", function(e) {
                	$(this).removeClass("focus");
                });

                $(".ui-widget.app.multimedia-gallery.collage").on("click keydown", ".mmg-article-link.watch-video", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        // GET ACTIVE RECORD INDEX
                        var recordIndex = $(this).attr("data-record-index");

                        function decodeIframeHtml(iframe) {
                            var textArea = document.createElement("textarea");
                            textArea.innerHTML = iframe;

                            return textArea.value;
                        }

                        function addUrlParameters(iframe) {
                            var addedParameters = "";

                            // YOUTUBE EMBED
                            if(iframe.indexOf("youtube.com/embed") > -1) {
                                if(iframe.indexOf("rel=") == -1) {
                                    addedParameters += "&rel=0";
                                }
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/youtube.com\/embed\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // VIMEO EMBED
                            if(iframe.indexOf("vimeo.com/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=1";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/vimeo.com\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            // TEACHERTUBE EMBED
                            if(iframe.indexOf("teachertube.com/embed/video/") > -1) {
                                if(iframe.indexOf("autoplay=") == -1) {
                                    addedParameters += "&autoplay=true";
                                }

                                if(iframe.indexOf("?") == -1) {
                                    // REMOVE THE FIRST "&"
                                    addedParameters = addedParameters.substr(1);

                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add ?)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&?' + addedParameters);
                                } else {
                                    // ADD PARAMETERS
                                    // MATCH: youtube.com/embed/(any character except space)(end match on ") - Replace: ($& is the matched value)(add the built parameters)
                                    iframe = iframe.replace(/teachertube.com\/embed\/video\/[^\s]+?[^"]+/ig, '$&' + addedParameters);
                                }
                            }

                            return iframe;
                        }

                        // ADD VIDEO DIALOG
                        $(".ui-widget.app.multimedia-gallery.collage").append('' +
                            '
' +
                                '
' +
                                    '
' +
                                        '


' +
                                        addUrlParameters(decodeIframeHtml(mmg.records[recordIndex].videoIframe)) +
                                    '' +
                                    'Close' +
                                '' +
                            '' +
                        '');

                        // FOCUS CLOSE BUTTON
                        $(".ui-widget.app.multimedia-gallery.collage .mmg-video-close").focus();
                    }
                });

                // CLOSE VIDEO
                $(".ui-widget.app.multimedia-gallery.collage").on("click keydown", ".mmg-video-close", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        var returnFocusIndex = $(this).attr("data-return-index");

                        // ANIMATE THE VIDEO OUT
                        $(".ui-widget.app.multimedia-gallery.collage .mmg-video-window").addClass("close-video");

                        setTimeout(function() {
                            // REMOVE THE VIDEO CONTAINER COMPLETELY
                            $(".ui-widget.app.multimedia-gallery.collage .mmg-video-window").remove();

                            // FOCUS WATCH VIDEO LINK
                            $(".ui-widget.app.multimedia-gallery.collage .collage-photo-link.video[data-record-index='" + returnFocusIndex + "']").focus();
                        }, 1000);
                    }

                    if(e.type == "keydown" && e.keyCode == _this.KeyCodes.tab) {
                        e.preventDefault();

                        var videoIframe = $(".ui-widget.app.multimedia-gallery.collage .mmg-video-viewer iframe")[0];
                        videoIframe.contentWindow.focus();
                    }
                });

                // CAPTURE VIDEO WINDOW FOCUS OUT
                $(".ui-widget.app.multimedia-gallery.collage").on("focusout", ".mmg-video-window", function() {
                    $(".ui-widget.app.multimedia-gallery.collage .mmg-video-close").focus();
                });

                $(".ui-widget.app.multimedia-gallery.collage").csArticleSlider({
                    "breakpoints": [480, 320],
                    "viewerNum": [1, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "transitionSpeed": .5, // SECONDS
                    "slideFullView": false,
                    "buildComplete": function() {
                        $(".ui-widget.app.multimedia-gallery.collage .cs-article-slider-control.back").insertBefore($(".ui-widget.app.multimedia-gallery.collage .cs-article-slider-control.next"));
                    },
                    "extend": function(config, element, ArticleSlider) {}
                });
            }
        },

