        "Search": function() {
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

					if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-outer").attr("aria-hidden", "false");
                        $("body").css("overflow", "hidden");

                        setTimeout(function() {
                        	$("#gb-search-input").focus();
                        }, 500);
                    } else {
                    	closeSearch();
                    }
                }
            });

            $("#gb-search-outer").click(function(e) {
            	e.preventDefault();

                closeSearch();
            })

            $("#gb-search").click(function(e) {

            });

            $(document).on("click keydown", "#gb-search-close", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    e.stopPropagation();

					closeSearch();
                }

                if(e.type == "keydown" && e.keyCode == _this.KeyCodes.tab && !e.shiftKey) {
                	e.preventDefault();

                    $("#gb-search-input").focus();
                }
            });

            $(document).on("click keydown", "#gb-search-button", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    $("#gb-search-form").submit();
                }
            });

            $("#gb-search-form").click(function(e) {
            	e.stopPropagation();
            }).submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#gb-search-input").keydown(function(e) {
            	if(e.keyCode == _this.KeyCodes.tab && e.shiftKey) {
                	e.preventDefault();

                    $("#gb-search-close").focus();
                }
            }).focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            function closeSearch() {
            	$("#gb-search-toggle").attr("aria-expanded", "false").focus();
                $("#gb-search-outer").attr("aria-hidden", "true");
                $("body").css("overflow", "");
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

