	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	    //SEARCH TEXT
	    $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "search...") {
				$(this).val("");
			}
		});
        $("label[for='sw-search-input']").text("Search...");
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("search...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
		}
	}

	function scrollingGlobalIcons() {
		var getWidth = $(window).width();
		//DOC custom global icons scrolling
		if('12' > 10 || getWidth < 1024) {
			$("#gb-global-icons-inner").css({"position":"absolute","display":"block"});
			$("#gb-global-icons-left-arrow, #gb-global-icons-right-arrow").css("display", "inline-block");
			var globalIconLength = ($(".global-icon").width()+25) * $(".global-icon").length - $("#gb-global-icons-inner").width();

            //DOC scroll icons left
            $("#gb-global-icons-left-arrow").on("keypress click", function(e) {
                if(e.keyCode == 13 || e.keyCode == 32 || e.type == "click") {
                    var position = $("#gb-global-icons-inner").position();
                    position = position.left+82;
                    clampedPosition = position.clamp(-globalIconLength, 0);
                    $("#gb-global-icons-inner").animate({ left: clampedPosition}, 250);
                }
            });

        	//DOC scroll icons right
            $("#gb-global-icons-right-arrow").on("keypress click", function(e) {
                if(e.keyCode == 13 || e.keyCode == 32 || e.type == "click") {
                	var position = $("#gb-global-icons-inner").position();
                    position = position.left-82;
                    clampedPosition = position.clamp(-globalIconLength, 0);
                    $("#gb-global-icons-inner").animate({ left: clampedPosition}, 250);
                }
            });
		}
	}

// GET BROWSER AND VERSION
function getBrowser(){
	var N = navigator.appName, ua=navigator.userAgent, tem;
	var M = ua.match(/(opera|chrome|safari|firefox|msie)\/?\s*(\.?\d+(\.\d+)*)/i);
	if(M && (tem= ua.match(/version\/([\.\d]+)/i))!= null) M[2] = tem[1];
	M = M ? [M[1], M[2]] : [N, navigator.appVersion, '-?'];
	return [M[0], M[1]];
}

var browser = getBrowser();
var browserName = browser[0].toLowerCase();
var browserVersion = Math.floor(parseInt(browser[1]));

/*
	* Returns a number whose value is limited to the given range.
	*
	* Example: limit the output of this computation to between 0 and 255
	* (x * 255).clamp(0, 255)
	*
	* @param {Number} min The lower boundary of the output range
	* @param {Number} max The upper boundary of the output range
	* @returns A number in the range [min, max]
	* @type Number
*/

Number.prototype.clamp = function(min, max) {
	return Math.min(Math.max(this, min), max);
};

