	function drawCanvas()  {  
		if($("#hp-slideshow-canvas").size()){
			var canvas = document.getElementById("hp-slideshow-canvas");
			//IF IE
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext('2d');
			draw(ctx);
		}
	}	
	function draw(ctx) {
		var gradient;

		// layer1/bottombackground
		ctx.save();
		ctx.scale(1,1);
		ctx.beginPath();
		ctx.moveTo(0.0, 356.3);
		ctx.bezierCurveTo(0.0, 356.3, 170.0, 345.8, 344.3, 388.1);
		ctx.lineTo(3.7, 387.9);
		ctx.bezierCurveTo(3.7, 387.9, 0.0, 387.9, 0.0, 383.8);
		ctx.bezierCurveTo(0.0, 379.8, 0.0, 356.3, 0.0, 356.3);
		ctx.closePath();
		ctx.fillStyle = "#000000";
		ctx.fill();

		// layer1/topbackground
		ctx.restore();
		ctx.beginPath();
		ctx.moveTo(890.0, 30.9);
		ctx.bezierCurveTo(890.0, 30.9, 675.0, 50.6, 532.4, 0.0);
		ctx.lineTo(887.1, 0.2);
		ctx.bezierCurveTo(887.1, 0.2, 890.0, 0.2, 890.0, 4.3);
		ctx.bezierCurveTo(890.0, 8.4, 890.0, 30.9, 890.0, 30.9);
		ctx.closePath();
		ctx.fillStyle = "#000000";
		ctx.fill();

		// layer1/Group

		// layer1/Group/topgradient
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 8.7);
		ctx.lineTo(0.0, 53.5);
		ctx.bezierCurveTo(68.6, 25.8, 229.3, -18.9, 477.0, 31.1);
		ctx.bezierCurveTo(667.4, 69.5, 801.0, 64.1, 890.0, 45.8);
		ctx.lineTo(890.0, 30.7);
		ctx.bezierCurveTo(716.7, 42.5, 602.0, 22.6, 531.0, 0.1);
		ctx.lineTo(8.6, 0.1);
		ctx.bezierCurveTo(0.0, 0.1, 0.0, 0.1, 0.0, 8.7);
		ctx.closePath();
		gradient = ctx.createLinearGradient(445.0, 0.1, 445.0, 59.9);
		gradient.addColorStop(0.00, "rgb(255, 255, 255)");
		gradient.addColorStop(1.00, "rgba(255, 255, 255,0)");
		ctx.fillStyle = gradient;
		ctx.fill();

		// layer1/Group/rightgradient
		ctx.beginPath();
		ctx.moveTo(890.0, 379.4);
		ctx.lineTo(890.0, 169.9);
		ctx.bezierCurveTo(869.8, 222.7, 840.6, 313.6, 845.6, 388.1);
		ctx.lineTo(881.4, 388.1);
		ctx.bezierCurveTo(890.0, 388.1, 890.0, 388.1, 890.0, 379.4);
		ctx.closePath();
		gradient = ctx.createLinearGradient(943.1, 214.5, 817.3, 364.4);
		gradient.addColorStop(0.00, "rgb(255, 255, 255)");
		gradient.addColorStop(0.85, "rgba(255, 255, 255,0)");
		gradient.addColorStop(1.00, "rgba(255, 255, 255,0)");
		ctx.fillStyle = gradient;
		ctx.fill();

		// layer1/Group/bottomrightgradient
		ctx.beginPath();
		ctx.moveTo(890.0, 379.4);
		ctx.lineTo(890.0, 335.7);
		ctx.bezierCurveTo(843.5, 317.1, 735.1, 326.9, 496.0, 382.7);
		ctx.bezierCurveTo(486.8, 384.7, 477.6, 386.5, 468.6, 388.1);
		ctx.lineTo(881.4, 388.1);
		ctx.bezierCurveTo(890.0, 388.1, 890.0, 388.1, 890.0, 379.4);
		ctx.closePath();
		gradient = ctx.createLinearGradient(468.6, 357.6, 890.0, 357.6);
		gradient.addColorStop(0.00, "rgb(255, 255, 255)");
		gradient.addColorStop(0.70, "rgba(255, 255, 255,0)");
		gradient.addColorStop(1.00, "rgba(255, 255, 255,0)");
		ctx.fillStyle = gradient;
		ctx.fill();

		// layer1/Group/bottomleftgradient
		ctx.beginPath();
		ctx.moveTo(348.3, 388.1);
		ctx.lineTo(606.1, 388.1);
		ctx.bezierCurveTo(267.9, 300.4, 55.0, 335.0, 0.0, 346.9);
		ctx.lineTo(0.0, 356.3);
		ctx.bezierCurveTo(133.2, 349.0, 280.2, 371.7, 348.3, 388.1);
		ctx.closePath();
		gradient = ctx.createLinearGradient(303.0, 330.1, 303.0, 388.1);
		gradient.addColorStop(0.00, "rgb(255, 255, 255)");
		gradient.addColorStop(0.70, "rgba(255, 255, 255,0)");
		gradient.addColorStop(1.00, "rgba(255, 255, 255,0)");
		ctx.fillStyle = gradient;
		ctx.fill();

		// layer1/Group/leftgradient
		ctx.beginPath();
		ctx.moveTo(0.0, 94.0);
		ctx.lineTo(0.0, 337.5);
		ctx.bezierCurveTo(3.6, 334.9, 7.0, 330.7, 10.3, 324.7);
		ctx.bezierCurveTo(44.0, 263.8, 23.3, 165.5, 0.0, 94.0);
		ctx.closePath();
		gradient = ctx.createLinearGradient(14.0, 94.0, 14.0, 337.5);
		gradient.addColorStop(0.00, "rgb(255, 255, 255)");
		gradient.addColorStop(0.80, "rgba(255, 255, 255,0)");
		gradient.addColorStop(1.00, "rgba(255, 255, 255,0)");
		ctx.fillStyle = gradient;
		ctx.fill();
		ctx.restore();
	}

    function addFramesetTranslate() {
