function translateMover() {
    if (window.innerWidth < 1100)
        $(".GoogleTranslate").appendTo("#mobile_top_nav");
    else
        $(".GoogleTranslate").appendTo(".HeaderQuickLinks");
}
$(document).ready(function () {
    translateMover();
});
$(window).resize(function () {
    translateMover();
});

