function SmoothScroll() {
    var height = $('.slideshow_outer_container').outerHeight();
    var topper = $('.slideshow_outer_container').position().top;
    window.scroll({
        top: height + topper,
        left: 0,
        behavior: 'smooth'
    });
}
$('.hpDiv2').parallax();

$(window).scroll(function () {
    var height = $('.slideshow_outer_container').outerHeight();
    var scrollHeight = $(window).scrollTop();
    var top = $('.slideshow_outer_container').position().top;
    $('.ContentContainerInner').removeClass('ContentTop')
    $('.ContentContainer').css('height', $('.ContentContainerInner').outerHeight() + 'px');
    if (height + top < scrollHeight || window.innerWidth <= 1100) {
        $('.ContentContainerInner').addClass('ContentTop')
    }
});
$(window).scroll();

