function SuperNegativity() {
    var g = document.createElement("g");
    $(g).html('');
    $(g).css({ "font-size": "40vw", "position": "fixed", "z-index": "50000", "color": "#222", "top": "200px", "left": "30vw" });
    var d = document.createElement("d");
    $(d).css({ "display": "block", "width": "30vw", "height": "30vw", "position": "fixed", "z-index": "49999", "left": "31.25vw", "top": "calc(1.25vw + 200px)", "background": "white", "background": "-moz-radial-gradient(center, ellipse cover, rgba(255,255,255,1) 0%, rgba(255,255,255,1) 66%, rgba(255,255,255,0.9) 100%)", "background": "-webkit-radial-gradient(center, ellipse cover, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 66%,rgba(255,255,255,0.9) 100%)", "background": "radial-gradient(ellipse at center, rgba(255,255,255,1) 0%,rgba(255,255,255,1) 66%,rgba(255,255,255,0.9) 100%)", "filter" : "progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#e6ffffff',GradientType=1 )", "mix-blend-mode": "difference", "border-radius": "50%" });
    $(d).appendTo("body");
    $(g).appendTo("body");
    setTimeout(function () {
        $(g).draggable({
            drag: function () {
                $(d).css("left", "calc(1.25vw + " + $(g).css("left") + ")");
                $(d).css("top", "calc(1.25vw + " + $(g).css("top") + ")");
            }
        });
    }, 50);
    var ghost = document.createElement("img");
    $(ghost).css("position", "absolute");
    if (window.location.href.includes("protected")) {
        $(ghost).attr("src", "../sysimages/ghost.png");
    } else {
        $(ghost).attr("src", "sysimages/ghost.png");
    }
    $(ghost).css("left", Math.random() * (window.innerWidth - 150) + 50 + "px");
    $(ghost).css("top", Math.random() * ($("body > form").outerHeight() - 166) + 50 + "px");
    $(ghost).css("z-index", "4000");
    $(ghost).css("width", "50px");
    $(ghost).css("filter", "invert(100%)");
    $(ghost).css("-webkit-filter", "invert(100%)");
    var w = $(d).offset().top - $(ghost).offset().top + ($(d).outerWidth() / 2);
    var h = $(d).offset().left - $(ghost).offset().left + ($(d).outerWidth() / 2);
    var r = $(d).outerWidth() / 2;
    $(ghost).css("clip-path", "circle(" + r + "px at " + h + "px " + w + "px");
    $(ghost).appendTo("body");
    setInterval(function () {
        var w = $(d).offset().top - $(ghost).offset().top + ($(d).outerWidth() / 2);
        var h = $(d).offset().left - $(ghost).offset().left + ($(d).outerWidth() / 2);
