var height = $('#content-container').height();
$('.fullheight').css('min-height', height + 'px');
$(window).resize(function () {
    $('.fullheight').css('min-height', '0px');
    var height = $('#content-container').height();
    $('.fullheight').css('min-height', height + 'px');
});

