								break;
							}
						}//for

						if ( levell > 0 ) {
							$(this).parent().addClass('sublv'+levell);
							$(this).parent().attr('lv',levell);
							$(this).parent().attr('stt',index);
							$(this).parent().addClass('stt'+index);						
