        label: 'ADA Menu',
        prependTo: '#mobile-top-nav',
        allowParentLinks: true,
        removeClasses: true
    });

    mobileButton.on("click", function () {
        topNav.slicknav('toggle');
    });

    // Search Panel
    const searchPanel = $("#ucTopNav_pnlSearch");
    const searchToggle = $("#search-toggle");
    const searchClose = $("#search-close");
    const searchBox = $("#ucTopNav_txtSearch");

    searchToggle.click(function () {
        searchPanel.addClass("togglebox__visible");
        searchBox.focus();
    });

    searchClose.click(function () {
        searchPanel.removeClass("togglebox__visible");
        searchBox.blur();
    });

