div.script:
    var $timer = $("#lblTimeoutTimer");
        function update() {
            var myTime = $timer.html();
            var ss = myTime.split(":");
            var dt = new Date();
            dt.setHours(0);
            dt.setMinutes(ss[0]);
            dt.setSeconds(ss[1]);

            var dt2 = new Date(dt.valueOf() - 1000);
            var temp = dt2.toTimeString().split(" ");
            var ts = temp[0].split(":");

            $timer.html(ts[1] + ":" + ts[2]);

            if ((ts[1] === "02") && (ts[2] === "00")) {
                //modal alert
                bootbox.confirm({
                    title: "Your session is about to expire due to inactivity",
                    message: "Would you like to continue your session?",
                    buttons: {
                        confirm: {
                            label: 'Yes',
                            className: 'btn-success'
                        },
                        cancel: {
                            label: 'No',
                            className: 'btn-danger'
                        }
                    },
                    callback: function (result) {
                        console.log('This was logged in the callback: ' + result);
                        if (result) location.reload();
                        else $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                    }
                });
            }
            if ((ts[1] === "00") && (ts[2] === "00")) {
                $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
            }
            else { setTimeout(update, 1000); }
        }

if (localStorage.getItem("ShowClosingsAndAlerts") == "false") {
    $("#css-only-modals").css("display", "none");
    if ($("#closings1_divAlertBanner").length > 0) {
        $("#AlertShow").css("display", "block");
    }
    setTimeout(function () {
        $("#css-only-modals").css("display", "block");
    }, 500);
    $(".css-only-modal").css("opacity", "0");
    $(".css-only-modal").css("pointer-events", "none");
    $("#css-only-modals").css("opacity", "0");
    $("#css-only-modals").css("pointer-events", "none");
    $("#screen-shade").css("opacity", "0");
    $("#screen-shade").css("pointer-events", "none");
} else {
    if ($("#closings1_divAlertBanner").length > 0) {
        $("#css-only-modals").css("display", "block");
    }
}
function SetSCAASession(s) {
    localStorage.setItem("ShowClosingsAndAlerts", s);
    if (s == "true") {
        $("#css-only-modals").css("display", "block");
        $("#AlertShow").css("opacity", "0");
        $("#AlertShow").css("pointer-events", "none");
        $(".css-only-modal").css("opacity", "1");
        $(".css-only-modal").css("pointer-events", "auto");
        $("#css-only-modals").css("opacity", "1");
        $("#css-only-modals").css("pointer-events", "auto");
        $("#screen-shade").css("opacity", "0.5");
        $("#screen-shade").css("pointer-events", "auto");
    } else {
        $("#AlertShow").css("display", "block");
        $("#AlertShow").css("opacity", "1");
        $("#AlertShow").css("pointer-events", "auto");
        $(".css-only-modal").css("opacity", "0");
        $(".css-only-modal").css("pointer-events", "none");
        $("#css-only-modals").css("opacity", "0");
        $("#css-only-modals").css("pointer-events", "none");
        $("#screen-shade").css("opacity", "0");
        $("#screen-shade").css("pointer-events", "none");
    }
}

link.script:
    if (!window.hasOwnProperty('webkitSpeechRecognition')) {
        $(".GoogleSearch span").remove();
        $(".GoogleSearch input").css("width", "calc(100% - 29px)");
    }
    function startDictation() {

        if (window.hasOwnProperty('webkitSpeechRecognition')) {

            var recognition = new webkitSpeechRecognition();

            recognition.continuous = false;
            recognition.interimResults = false;

            recognition.lang = "en-US";
            recognition.start();

            $(".header_search_box")[0].placeholder = "Listening...";
            $(".header_search_box")[1].placeholder = "Listening...";
            recognition.onresult = function (e) {
                $(".header_search_box")[0].placeholder = "";
                $(".header_search_box")[0].value = e.results[0][0].transcript;
                $(".header_search_box")[1].placeholder = "";
                $(".header_search_box")[1].value = e.results[0][0].transcript;
                recognition.stop();
                $(".header_search_button")[0].click();
            };

            recognition.onerror = function (e) {
                recognition.stop();
            }

            recognition.onend = function (e) {
                $(".header_search_box")[0].placeholder = "Search...";
                $(".header_search_box")[1].placeholder = "Search...";
            }
        }
    }

