div.script:
    $(function () {
        function _submitSearch() {
            var q = $('#q').val();
            q = q.replace(/\{|\}|\(|\)|\[|\]|\*|\^|\!|\~|\?|\//g, "");

            location.href = "/sys/search?q=" + window.encodeURIComponent(q);
        };

        $("#q").on("keypress", function (event) {
            if ((event.which == 13 || event.keyCode == 13) && this.value.trim() !== "" ){
                _submitSearch();
            }
        });

        $("#q").next().on("click", _submitSearch);
    });

link.script:
    var slideshowID = 'angelo_slideshow_5410e4ed18864e8ab409a89ecb0390ce';
    var duration = 9000;

    /* Demo Scripts for Bootstrap Carousel and Animate.css article
    * on SitePoint by Maria Antonietta Perna
    */
    (function ($) {
        $(document).ready(function () {
            //Function to animate slider captions
            function doAnimations(elems) {
                //Cache the animationend event in a variable
                var animEndEv = 'webkitAnimationEnd animationend';

                elems.each(function () {
                    var $this = $(this),
                        $animationType = $this.data('animation');
                    $this.addClass($animationType).one(animEndEv, function () {
                        $this.removeClass($animationType);
                    });
                });
            }

            //Variables on page load
            var $myCarousel = $('#' + slideshowID),
                $firstAnimatingElems = $myCarousel.find('.item:first').find("[data-animation ^= 'animated']");

            //Initialize carousel
            $myCarousel.carousel({
                interval: duration
            });

            //Animate captions in first slide on page load
            doAnimations($firstAnimatingElems);

            //Pause carousel
            // $myCarousel.carousel('pause');

            //Other slides to be animated on carousel slide event
            $myCarousel.on('slide.bs.carousel', function (e) {
                var $animatingElems = $(e.relatedTarget).find("[data-animation ^= 'animated']");
                doAnimations($animatingElems);
            });
        });
    })(jQuery);

div.script:
    (function($) {

        var customAddBtn = {};
        var authenticatedUser = false;
        var showWeekends = true;
        var views = 'agendaDay,listCustom';
        var widgetId = '5c22a6fb12e04ff8affe1a6daca76542';
        var defaultView = 'listCustom';
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth() + 1; //January is 0!
        var yyyy = today.getFullYear();
        var widgetTimeFormat = 'H(:mm)'; //24hrs vs 12hrs format

        if (dd < 10) {
            dd = '0' + dd;
        }

        if (mm < 10) {
            mm = '0' + mm;
        }

        today = yyyy + '-' + mm + '-' + dd;


        window.reloadCalendarEvents = function () {
            $(".fc").each(function () {
                $(this).fullCalendar('refetchEvents');
            })
        }


        $(document).ready(function() {

            // init: function(themeSystem) {
            $('#calendar_5c22a6fb12e04ff8affe1a6daca76542').fullCalendar({
                contentHeight: function () {
                    return $('#calendar_5c22a6fb12e04ff8affe1a6daca76542').width() * 1.1;
                },
                views: {
                    listCustom: {
                        type: 'list',
                        visibleRange: function(currentDate) {
                            return {
                                start: currentDate,
                                end: currentDate.clone().add(30, 'days')
                            };
                        },
                        buttonText: 'list'
                    }
                },
                themeSystem: 'bootstrap3',
                header: {
                    left: 'prev, next', //today
                    center: 'title',
                    right: 'add ' + views
                },
                defaultView: defaultView,
                defaultDate: today,
                navLinks: true, // can click day/week names to navigate views
                editable: false,
                eventLimit: true, // allow "more" link when too many events
                weekNumbers: false, // show week number on the left column
                weekends: showWeekends, // show/hide weekends  (sat and Sun)
                //timeFormat: 'H(:mm)', // widgetTimeFormat,// 24hr/12hr settings
                firstDay: Number(0),
                //****** Events ***********
                eventClick: function(calEvent, jsEvent, view) {                  
                    ShowEventModal(calEvent.id, calEvent.start, calEvent.end);
                },
                eventDrop: function(event, delta, revertFunc) {
                },
                eventResize: function(event, delta, revertFunc) {
                },
                eventAfterRender: function (event, $el) {
                    // give a href value to event's hyperlink so can be "tabbed" into focus
                    $el.parent().find("a").attr("href", "#").prop("href", "#");
                },
                dayClick: function(date) {
                    // alert('clicked' + date.format());
                },
                select: function(start, end) {
                    //alert('selected ' + start.format() + ' to ' + end.format());
                },
                eventMouseover: function(event, jsEvent, view) {
                    var thisObject = this;
                    ShowEventDetailsTooltip(event, jsEvent, view, thisObject);
                },
                customButtons: customAddBtn,
                events: function(start, end, timezone, callback) {
                    //debugger;
                    $.ajax({                      
                        url: '/sys/widgets/calendar/events',
                        dataType: 'json',
                        data: {
                            // our hypothetical feed requires UNIX timestamps
                            widgetId: widgetId,
                            from: start.format(),
                            to: end.format()
                        },
                        success: function(doc) {
                            callback(doc.result);
                        }
                    });
                },
                eventDataTransform: function(event) {
                    //workaround for all days that span more than 1 day.
                    if(event.allDay) {
                        event.end = moment(event.end).add(1, 'days');
                    }
                    return event;  
                }     

            });

            //security
            $.get("/sys/calendar/security/canaddevents",
                function(result) {
                    //debugger;
                    authenticatedUser = result.isAuthenticated;
                    if (result.isAuthorized) {
                        ShowAddEventsButton()
                    }
                })
        });

        function ShowEventDetailsTooltip(event, jsEvent, view, thisObj) {

            var allDayHeader = "";
            var startDate = "";
            var endDate = "";
            if (event.start != null) {
                startDate = event.allDay ? event.start.format("MMM D") : event.start.format("MMM D h:mm a");
            }
            if (event.end != null) {
                
                if (event.allDay && event.start < event.end) {
                    event.end = moment(event.end).add(-1, 'days');
                }
                endDate =  event.allDay ? event.end.format("MMM D") : event.end.format("MMM D h:mm a");
            }
            if (event.allDay) {
                allDayHeader = "
All Day";
            }

            var location = "";
            if (event.location) {
                location = "
" + event.location + "";
            }

            var phone = "";
            if (event.phone && event.showPhoneNumber) {
                phone = "
" + event.phone + "";
            }

            var userName = "";
            if (event.userName && event.showOrganizerName) {
                userName = "
" + event.userName + "";
            }

            var desc = "";
            if (event.description) {
                desc = event.description;
            }
            var display = allDayHeader +
                "

" +
                event.title +
                "
" +
                startDate +
                " - " +
                endDate +
                "
" +
                decodeURI(desc) +
                "" +
                location +
                userName +
                phone +
                "";


            $(thisObj).tooltip({
                title: display,
                html: true,
                container: 'body'
            });
            $(thisObj).tooltip('show');
        }

        function ShowAddEventsButton() {
            $('#calendar_5c22a6fb12e04ff8affe1a6daca76542').fullCalendar('option', {
                customButtons: {
                    add: {
                        text: 'Add',
                            click: function () {
                            ShowAddEventModal();
                        }
                    }
                }
            });
        }

        var ShowEventModal = function (id, start, end) {
            $.dialog("/sys/calendar/event/modal", { id: id, start: start.format("L LT"), end: end.format("L LT")});
        }

        var ShowAddEventModal = function () {
            $.console("calendar", "/sys/console/calendar/event/create");
        }


   })(jQuery);

body.script:
    $(document).ready(function () {

    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
