    $(document).ready(function () {
        $("#dEvents").click(function () {
            if (!$("#dEvents").hasClass("Open")) {
                $("#dEvents").addClass("Open");
                $("#aEvents").removeClass("Open");
                $("#DistrictEvents").addClass("Open");
                $("#AthleticEvents").removeClass("Open");
                $("#EventArrows2").removeClass("Open");
                $("#EventArrows1").addClass("Open");
            }
        });
        $("#aEvents").click(function () {
            if (!$("#aEvents").hasClass("Open")) {
                $("#aEvents").addClass("Open");
                $("#dEvents").removeClass("Open");
                $("#AthleticEvents").addClass("Open");
                $("#DistrictEvents").removeClass("Open");
                $("#EventArrows1").removeClass("Open");
                $("#EventArrows2").addClass("Open");
            }
        });
    });

 





