    var downHover = false;
    var downTimer = 0;
    var downMaxTimer = 60;
    var downDelay = 150;
    $(document).ready(function () {
        $(".hpPlay a").mouseover(function () {
            downHover = true;
        });
        $(".hpPlay a").mouseout(function () {
            downHover = false;
        });
    });
    setInterval(function () {
        if (downHover || downTimer > 0) {
            downTimer++;
            var w = 120 + (90 * downTimer / downMaxTimer);
            var h = 120 + (90 * downTimer / downMaxTimer);
            var o = (((downMaxTimer) - (downTimer)) / downMaxTimer);
            $("#pulse").css("display", "block");
            $("#pulse").css("width", w + "px");
            $("#pulse").css("height", h + "px");
            $("#pulse").css("left", "calc(50%" + (-w) + "px)");
            $("#pulse").css("top", "calc(50%" + ((-h) + 60) + "px)");
            $("#pulse").css("margin-left", (-w/2) + 60 + "px");
            $("#pulse").css("margin-top", (-h/2) + 60 + "px");
            $("#pulse").css("opacity", o);
            if (downTimer >= downMaxTimer) {
                $("#pulse").css("display", "");
                $("#pulse").css("width", "");
                $("#pulse").css("height", "");
                $("#pulse").css("left", "");
                $("#pulse").css("top", "");
                $("#pulse").css("opacity", "");
                $("#pulse").css("margin-left", "");
                $("#pulse").css("margin-top", "");
            }
            if (downTimer >= downMaxTimer + downDelay) {
                downTimer = 0;
                $("#pulse").css("display", "");
                $("#pulse").css("width", "");
                $("#pulse").css("height", "");
                $("#pulse").css("left", "");
                $("#pulse").css("top", "");
                $("#pulse").css("opacity", "");
                $("#pulse").css("margin-left", "");
                $("#pulse").css("margin-top", "");
            }
        }
    }, 1);

