    $(".hpDivSchools a").hover(function () {
        $(this).find(".YellowOverlay").addClass("In");
        $(this).find(".YellowOverlay").removeClass("Out");
        $(this).find(".YOButton").addClass("In");
        $(this).find(".YOButton").removeClass("Out");
    }, function () {
        $(this).find(".YellowOverlay").addClass("Out");
        $(this).find(".YellowOverlay").removeClass("In");
        $(this).find(".YOButton").addClass("Out");
        $(this).find(".YOButton").removeClass("In");
        var x = this;
        setTimeout(function () {
            $(x).find(".YellowOverlay").removeClass("Out");
            $(x).find(".YOButton").removeClass("Out");
        }, 900);
    });






            
        


            
        
    

