var ratioHeight = 320;
var ratioWidth = 560;
var videoContainerClass = "hp-video";
function videoResize2() {
    var ratio = ratioHeight / ratioWidth;
    var wRatio = window.innerHeight / window.innerWidth;
    if (ratio >= wRatio) {
        $("." + videoContainerClass).addClass("Wide");
    } else {
        $("." + videoContainerClass).removeClass("Wide");
    }
    var videoWidth = $(".hp-video").outerWidth();
    var windowWidth = window.innerWidth;
    if (videoWidth > windowWidth) {
        $(".hp-video").css("margin-left", ((videoWidth - windowWidth) / -2) + "px");
    }
    else {
        $(".hp-video").css("margin-left", "");
    }
}
$(window).resize(function () {
    videoResize2();
});
$(document).ready(function () {
    videoResize2();
});

