$(document).ready(function () {
    $('#btn_news').click(function () {
        $('#news').css("z-index", "2");
        $('#events').css("z-index", "-1");
        $('.tab_buttons li.EventsButton').css("background-color", "#B3A571");
        $('.tab_buttons li.NewsButton').css("background-color", "#EDEDED");
        $('.NewsButton hr').css("display", "block");
        $('.EventsButton hr').css("display", "none");
    });
    $('#btn_events').click(function () {
        $('#news').css("z-index", "-1");
        $('#events').css("z-index", "2");
        $('.tab_buttons li.EventsButton').css("background-color", "#EDEDED");
        $('.tab_buttons li.NewsButton').css("background-color", "#B3A571");
        $('.NewsButton hr').css("display", "none");
        $('.EventsButton hr').css("display", "block");
    });
});
