    },
    exited: function(direction) {

    }
})


$(document).ready(function() {


        var headerHeight = $('html #header').outerHeight();
        var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
        var eightypercent = 30 / 100 * $(window).outerHeight();
        var sliderHeight = $(window).outerHeight() - eightypercent;
        //$('.slider-container img, #slider').css( "height", sliderHeight);
        $('.slider-container img').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px;');
        $('#slider').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px; margin-top:' + headerHeight + 'px;');
        var sliderheader = $('html #slider').outerHeight() + headerHeight;
        var Aboutmargintop = sliderHeight + headerHeight;
        $('body .main .about-wrapper').attr('style', 'margin-top:' + sliderheader + 'px;');


    $(window).on('resize', function() {

            var headerHeight = $('html #header').outerHeight();
            var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
            var eightypercent = 30 / 100 * $(window).outerHeight();
            var sliderHeight = $(window).outerHeight() - eightypercent;
            //$('.slider-container img, #slider').css( "height", sliderHeight);
           $('.slider-container img').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px;');
        $('#slider').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px; margin-top:' + headerHeight + 'px;');
            var sliderheader = $('html #slider').outerHeight() + headerHeight;
            var Aboutmargintop = sliderHeight + headerHeight;
            $('body .main .about-wrapper').attr('style', 'margin-top:' + sliderheader + 'px;');

    });

    $('.slider-container').bxSlider({
        mode: 'fade',
        auto: true,
        autoHover: false,
        adaptiveHeight: true,
        speed: 1000,
        pause: 6000,
        onSliderLoad: function(currentIndex) {
            var currentIndexItem = $('.slider-container .slide').eq(currentIndex);
            currentIndexItem.addClass('active-slide');

        },
        onSlideAfter: function($slideElement) {
            $('.slider-container .slide').removeClass('active-slide');
            $slideElement.addClass('active-slide');
            $('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');

                var headerHeight = $('html #header').outerHeight();
                var bulletinBoardHeight = $('#slider .slider-info').outerHeight() + 100;
                var eightypercent = 30 / 100 * $(window).outerHeight();
                var sliderHeight = $(window).outerHeight() - eightypercent;
                //$('.slider-container img, #slider').css( "height", sliderHeight);
              $('.slider-container img').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px;');
        $('#slider').attr('style', 'height:' + sliderHeight + 'px; min-height:' + bulletinBoardHeight + 'px; margin-top:' + headerHeight + 'px;');
                var sliderheader = $('html #slider').outerHeight() + headerHeight;
                var Aboutmargintop = sliderHeight + headerHeight;
                $('body .main .about-wrapper').attr('style', 'margin-top:' + sliderheader + 'px;');

        }

    });

    var slider = $('.left-image').bxSlider({
        mode: 'fade',
        infiniteLoop: false,
        hideControlOnEnd: true,
        adaptiveHeight: true,
        slideMargin: 30,
        spped: 300,
        //auto: true,
        //autoHover: true,
        pager: 'true',
        onSliderLoad: function(currentIndex) {
            $('.news-wrapper .news').eq(1).trigger('mouseover');
            var currentIndexItem = $('.news-slider').find('.bx-viewport').find('.left-image').children().eq(currentIndex);
            currentIndexItem.addClass('active-slide');
            if (currentIndexItem.height() < $('.news-wrapper .right-content').height()) {
                $('.news-wrapper .bx-wrapper').addClass('verticalAlign');
            } else {
                $('.news-wrapper .bx-wrapper').removeClass('verticalAlign');
            }
            $('.news-wrapper .news').eq(currentIndex).addClass('active');

        },
        onSlideBefore: function($slideElement) {

            $('.news-slider').find('.bx-viewport').find('.left-image').children().removeClass('active-slide');
            $slideElement.addClass('active-slide');
            if ($slideElement.height() < $('.news-wrapper .right-content').height()) {
                $('.news-wrapper .bx-wrapper').addClass('verticalAlign');
            } else {
                $('.news-wrapper .bx-wrapper').removeClass('verticalAlign');
            }
            var indeximage = $slideElement.index();
            $('.news-wrapper .news').removeClass('active open');
            $('.news-wrapper .news').eq(indeximage).addClass('open');
        }
    });

    $(document).on('mouseenter', '.news-wrapper .news', function(e) {
        var indexitem = $(this).index();
        $(".news-slider .bx-pager a").eq(indexitem).trigger("click");
        $('.news-wrapper .news').removeClass('active open');
        $(this).stop().addClass('open');

    });
    var windowheight = $(window).height();
    $('.main .about-wrapper').css("min-height", windowheight);

    $(window).resize(function() {
        var windowheight = $(window).height();

        $('.main .about-wrapper').css("min-height", windowheight);

    });


});
