meta.script:
    WebFontConfig = {
        google: {
            families: ['Homenaje::latin', 'Open+Sans::latin',  'Shadows+Into+Light::latin'] }
    };

    (function() {
        var wf = document.createElement('script');
        wf.src = ('https:' == document.location.protocol ? 'https' : 'http') +
            '://ajax.googleapis.com/ajax/libs/webfont/1/webfont.js';
        wf.type = 'text/javascript';
        wf.async = 'true';
        var s = document.getElementsByTagName('script')[0];
        s.parentNode.insertBefore(wf, s);
    })();

link.script:
var cb = function() {
    var async_stylesheets = new Array(
        "",
        "http://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" // ALWAYS LOAD THIS STYLE LAST
    );
    for (i in async_stylesheets) {
        var l = document.createElement('link');
        l.rel = 'stylesheet';
        l.href = async_stylesheets[i];
        var h = document.getElementsByTagName('head')[0];
        h.parentNode.insertBefore(l, h);
    }
};
var raf = requestAnimationFrame || mozRequestAnimationFrame ||
    webkitRequestAnimationFrame || msRequestAnimationFrame;
if (raf) raf(cb);
else window.addEventListener('load', cb);

