    function Randomize() {
        $.each($("img[src*=sysimages]"), function (index, value) {
            var imgObj = new Image();
            imgObj.src = $(value)[0].src;
            var canvas = document.createElement("canvas");
            canvas.width = $(value).outerWidth();
            canvas.height = $(value).outerHeight();
            canvas.getContext('2d').drawImage(imgObj, 0, 0, $(value).outerWidth(), $(value).outerHeight());
            var imgData = canvas.getContext('2d').getImageData(0, 0, $(value).outerWidth(), $(value).outerHeight());
            var pixelData = imgData.data;
            var indexArray = [];
            for (var i = 0; i < pixelData.length / 4; i++) {
                indexArray.push(i);
            }
            indexArray = shuffle(indexArray);
            for (var i = 0; i < pixelData.length; i += 4) {
                imgData.data[i] = pixelData[indexArray[i / 4] * 4]; //Red
                imgData.data[i + 1] = pixelData[indexArray[i / 4] * 4 + 1];  //Green
                imgData.data[i + 2] = pixelData[indexArray[i / 4] * 4 + 2]; //Blue;
            }
            canvas.getContext('2d').putImageData(imgData, 0, 0);
            $(value).attr("src", canvas.toDataURL());
        });
        if (!window.location.href.includes("localhost:")){
