$(document).click(function (e) {
    if ($(e.target).parents(".SlideButton").length > 0) {
        if ($($(e.target).parents(".SlideButton")[0]).find(".Content").hasClass("Open")) {
            $(".Content").removeClass("Open");
            $(".Arrow").removeClass("Open");
            $($(e.target).parents(".SlideButton")[0]).find(".Content").addClass("Open");
            $($(e.target).parents(".SlideButton")[0]).find(".Arrow").addClass("Open");
        }
        else {
            $(".Content").removeClass("Open");
            $(".Arrow").removeClass("Open");
        }
    }
    else {
        $(".Content").removeClass("Open");
        $(".Arrow").removeClass("Open");
    }
});
function ArrowClick(arrow) {
    if ($(arrow).hasClass("Open")) {
        $(arrow).parent().find(".Content").removeClass("Open");
        $(arrow).removeClass("Open");
    } else {
        $(".Content").removeClass("Open");
        $(".Arrow").removeClass("Open");
        $(arrow).parent().find(".Content").addClass("Open");
        $(arrow).addClass("Open");
    }
}

