function KCode() {
    $(".scene").css("display", "block");
    setTimeout(Roll, 10);
}
var rolls = {
    1: {
        "Amount": 0,
        "Percent": 0
    },
    2: {
        "Amount": 0,
        "Percent": 0
    },
    3: {
        "Amount": 0,
        "Percent": 0
    },
    4: {
        "Amount": 0,
        "Percent": 0
    },
    5: {
        "Amount": 0,
        "Percent": 0
    },
    6: {
        "Amount": 0,
        "Percent": 0
    },
    "Total Rolls": 0
}
function Roll() {
    var x;
    var y;
    var z;
    if (Math.random() < 0.6667) {
        x = Math.floor(Math.random() * 8) * 360;
        y = Math.floor(Math.random() * 8) * 90;
        z = Math.floor(Math.random() * 8) * 360;
    }
    else {
        y = Math.floor(Math.random() * 8) * 360;
        x = Math.floor(Math.random() * 8) * 90;
        z = Math.floor(Math.random() * 8) * 360;
        if (x % 180 == 0) {
            if (Math.random() > 0.5) {
                x -= 90;
            }
            else {
                x += 90;
            }
        }
    }
    rolls["Total Rolls"]++;
    if (x % 360 == 0) {
        switch (y % 360) {
            case 0:
                rolls[1]["Amount"]++;
                break;
            case 90:
                rolls[4]["Amount"]++;
                break;
            case 180:
                rolls[3]["Amount"]++;
                break;
            case 270:
                rolls[2]["Amount"]++;
                break;
        }
    }
    else if (x % 360 == 90) {
        rolls[6]["Amount"]++;
    }
    else {
        rolls[5]["Amount"]++;
    }
    rolls[1]["Percent"] = rolls[1]["Amount"] * 100 / rolls["Total Rolls"];
    rolls[2]["Percent"] = rolls[2]["Amount"] * 100 / rolls["Total Rolls"];
    rolls[3]["Percent"] = rolls[3]["Amount"] * 100 / rolls["Total Rolls"];
    rolls[4]["Percent"] = rolls[4]["Amount"] * 100 / rolls["Total Rolls"];
    rolls[5]["Percent"] = rolls[5]["Amount"] * 100 / rolls["Total Rolls"];
    rolls[6]["Percent"] = rolls[6]["Amount"] * 100 / rolls["Total Rolls"];
    $(".scene .cube").css("transform", "translateZ(-100px) rotateY(" + y.toString() + "deg) rotateX(" + x.toString() + "deg) rotateZ(" + z.toString() + "deg)");
}

