div.script:
(function(d, s, id) {
			  var js, fjs = d.getElementsByTagName(s)[0];
			  if (d.getElementById(id)) return;
			  js = d.createElement(s); js.id = id;
			  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.7";
			  fjs.parentNode.insertBefore(js, fjs);
			}(document, 'script', 'facebook-jssdk'));

body.script:
$(function() {

	$('#topnav').accessibleMegaMenu();

	$('.slick-news').slick({
		arrows: true,
		slidesToShow: 3,
		slidesToScroll: 1,
		variableWidth: false,
		lazyLoad: 'ondemand',
		responsive: [
			{
				breakpoint: 1024,
				settings: {
					slidesToShow: 2,
				}
			},
			{
				breakpoint: 768,
				settings: {
					slidesToShow: 1,
				}
			}
		]
	});

	$('.slick-events').slick({
		arrows: true,
		slidesToShow: 3,
		slidesToScroll: 1,
		variableWidth: false,
		lazyLoad: 'ondemand',
		responsive: [
			{
				breakpoint: 1024,
				settings: {
					slidesToShow: 2,
				}
			},
			{
				breakpoint: 768,
				settings: {
					slidesToShow: 1,
				}
			}
		]
	});
});

window.onload = init;
var topnavButton = document.getElementById('topnav_mobile_toggle');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
