        currentPagePath: '/content/schools/ispa/en',
        label: 'News',
        listFrom: 'solr',
        limit: '10',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'e2430c17-536c-4063-9d8d-07090808b692',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

